import { IconType } from 'react-icons'

interface Action {
  icon: IconType
  label: string
  onPress: () => void
  disabled?: boolean
  destructive?: boolean
}

interface PrimaryActionRowWebProps {
  actions: Action[]
  title?: string
}

export function PrimaryActionRowWeb({ actions, title }: PrimaryActionRowWebProps) {
  return (
    <div className="space-y-2">
      {title && (
        <h3 className="text-lg font-semibold text-gray-900 dark:text-white px-1">
          {title}
        </h3>
      )}
      <div className="flex gap-3">
        {actions.map((action, index) => {
          const Icon = action.icon
          const iconColor = action.destructive ? 'text-red-600' : 'text-gray-700 dark:text-gray-200'
          const textColor = action.destructive ? 'text-red-600' : 'text-gray-700 dark:text-gray-200'
          const hoverBg = action.destructive ? 'hover:bg-red-50 dark:hover:bg-red-900/20' : 'hover:bg-gray-100 dark:hover:bg-gray-700'

          return (
            <button
              key={index}
              onClick={action.onPress}
              disabled={action.disabled}
              className={`flex-1 flex flex-col items-center justify-center gap-2
                         py-4 px-3 rounded-xl border
                         bg-white dark:bg-gray-800
                         border-gray-200 dark:border-gray-700
                         ${hoverBg}
                         disabled:opacity-50 disabled:cursor-not-allowed
                         transition-colors duration-150
                         focus:outline-none focus:ring-2 focus:ring-blue-500`}
            >
              <Icon className={`text-[28px] ${iconColor}`} />
              <span className={`text-sm font-medium ${textColor}`}>
                {action.label}
              </span>
            </button>
          )
        })}
      </div>
    </div>
  )
}

