'use client'
import { useState } from 'react'
import { NoteActionsModalWeb } from '@/components/note-actions-modal-web'
import { Toaster } from 'sonner'

export default function NotesPage() {
  const [showModal, setShowModal] = useState(false)
  const [selectedNote, setSelectedNote] = useState<Note | null>(null)

  const handleNoteOptions = (note: Note) => {
    setSelectedNote(note)
    setShowModal(true)
  }

  const handleToggleFavorite = async () => {
    if (!selectedNote) return
    // API call to toggle favorite
    await fetch(`/api/notes/${selectedNote.id}/favorite`, { method: 'PATCH' })
    // Update local state
  }

  const handleTitleChange = async (newTitle: string) => {
    if (!selectedNote) return
    // API call to update title
    await fetch(`/api/notes/${selectedNote.id}`, {
      method: 'PATCH',
      body: JSON.stringify({ title: newTitle })
    })
  }

  return (
    <div>
      <Toaster />

      {/* Note list with options button */}
      {notes.map((note) => (
        <div key={note.id}>
          <NoteItem note={note} />
          <button onClick={() => handleNoteOptions(note)}>
            (...)
          </button>
        </div>
      ))}

      {/* Action Modal */}
      {selectedNote && (
        <NoteActionsModalWeb
          visible={showModal}
          onClose={() => setShowModal(false)}
          noteId={selectedNote.id}
          noteTitle={selectedNote.title}
          noteContent={selectedNote.content}
          isFavorite={selectedNote.isFavorite}
          onToggleFavorite={handleToggleFavorite}
          onTitleChange={handleTitleChange}
        />
      )}
    </div>
  )
}

